import { Component } from '@angular/core';
import { AuthService } from '@auth0/auth0-angular';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'webapp';
  goto(id:any){
    
  }
  constructor(public auth: AuthService){

  }

  loginWithRedirect(){

    
     this.auth.loginWithRedirect(
      {
        appState: { target: '/predictions' }
      }
    ) 
  }

  logout(): void {
    console.log("hi hellooo", this.auth.isAuthenticated$)
    this.auth.logout({ returnTo: window.location.origin });

  }
  // isAuthenticated: boolean = false;
  // constructor(public oktaAuth: OktaAuthService){
  //   this.oktaAuth.$authenticationState.subscribe(
  //     (isAuthenticated: boolean) => this.isAuthenticated = isAuthenticated
  //   )
  // }
  ngOnInit() {
    // this.oktaAuth.isAuthenticated().then((auth) => {this.isAuthenticated = auth});
    console.log()
  }

  // login() {
  //   this.oktaAuth.loginRedirect();
  // }

  // logout() {
  //   this.oktaAuth.logout('/');
  // }
}
<div id="app">
  <div class="container p-0">
    <div class="row"></div>
    <div
      *ngIf="(auth.isAuthenticated$ | async) === false"
      class="nav navbar-nav navbar-right col-sm-10 d-flex justify-content-end">
      <p class="col-sm-2 m-0">
        <a href="/signup"><button class="btn btn-primary">Sign Up</button></a>
      </p>
      <p class="col-sm-2 m-0">
        <button (click)="loginWithRedirect()" class="btn btn-primary">
          Sign In
        </button>
      </p>
    </div>
    <div *ngIf="(auth.isAuthenticated$ | async) === true">
      <p class="col-sm-2 m-0">
        <button (click)="logout()" class="btn-primary sign-in">logout</button>
      </p>
    </div>
  </div>
  <router-outlet></router-outlet>
</div>
